import React from "react";

const CampoTexto = (props) => {
    // criar uma função para armazenar o valor do input 
    // valorInput = event.target.value
    // chamar com 'onChange={função}' 

    let valorInput = "";
    const pegarValorDoInput = (event) => {
        valorInput = event.target.value;
    }

    return (
        <div>
            <label>{props.label}</label>
            <input value={valorInput} onChange={pegarValorDoInput} required={props.obrigatorio} placeholder={props.placeholder}></input>
        </div>
    )
}
export default CampoTexto;
