import React from "react";

const Lista = (props) => {
    return(
        <div className="flex flex-col gap-4">
            <label>{props.label}</label>  
            <select onChange={event => props.seAlterado(event.target.value)} value={props.valor} required={props.obrigatorio} className="bg-white p-2 rounded-md"> 
                {props.itens.map(item => <option key={item} className="bg-gray-100">{item}</option>)}
            </select>
        </div>
    )
}
export default Lista;
