import React, { useState } from "react";
import CampoTexto from "../CampoTexto";
import Lista from "../listaPosição";
import Botao from "../botao"

const Formulario = (props) => {
    const posicao = [
        "Atacante",
        "Goleiro",
        "Zagueiro",
        "Tecnico",
        "Capitão"
    ]
    const times = [
        "Brasil",
        "Argentina",
        "França",
        "Alemanha"
    ]
    const [nome, setNome] = useState("");
    const [imagem,setImagem] = useState("");
    const [cargo, setCargo] = useState([]);
    const [time, setTime] = useState([]);

    const aoSalvar = (evento) => {
        //previne o comportamento padrão de recarregar a pagina ao submeter o formulario
        evento.preventDefault() 
        props.colaboradorNovo({
            nome,
            cargo,
            time,
            imagem
    })
        console.log(nome, imagem, cargo, time)
    }

    return(
        <section>
            <form onSubmit={aoSalvar} className="m-8 p-10 bg-gray-100 shadow-md flex flex-col justify-center gap-4"> 
            <h1>Preencha os campos para escalar seus times!</h1>
                <CampoTexto 
                    obrigatorio={true} 
                    placeholder="Nome" 
                    label="Nome"
                    valor= {nome}
                    aoAlterado={valor => setNome(valor)}
                />

                <CampoTexto
                    placeholder="url da imagem" 
                    label="url da imagem"
                    valor={imagem}
                    aoAlterado={valor => setImagem(valor)}
                />
                <Lista
                    obrigatorio={true} 
                    label="Posição" 
                    itens={posicao}
                    valor={cargo}
                    aoAlterado={valor => setCargo(valor)}
                />
                <Lista 
                    obrigatorio={true} 
                    label="Seleção" 
                    itens={times}
                    valor={time}
                    aoAlterado={valor => setTime(valor)}
                />
                <Botao>
                    criar card
                </Botao>
            </form>
        </section>
    )
}
export default Formulario;
