import React from "react";
import Card from "../card";

const Times = ({atletas, times, mudarCor, atletaa, setAtleta}) => {
    console.log(time)
    return(
        atletas.length > 0 && time &&  <section style={{backgroundColor: time.corSecundaria}} className="p-12 text-center ">
           <input type="color" onChange={evento => mudarCor(evento.target.value, time.nome)} value={time.corSecundaria}></input>
           <h3  className="p-3 m-3"> {time.nome}</h3>
           <div className="flex justify-around flex-wrap gap-3">
                {atletas.map(atleta => 
                    <Card 
                        key={atleta.index}
                        atleta={atletaa}
                        setAtleta={setAtleta} 
                        nome={atleta.nome} 
                        cargo={atleta.cargo} 
                        imagem={atleta.imagem}
                        index={atleta.index}
                        titularReserva={atleta.titularReserva}
                    />
                )}
           </div>
        </section>
    )
}
export default Times;
