import React from "react";
import './card.css';

const Card = (props) => {
    const removerJogador = () => {
        const novosJogadores = [...props.atleta];
        const jogador = props.atleta.find((atleta) => atleta.index === props.index)
        const index = props.atleta.indexOf(jogador)
        novosJogadores.splice(index, 1);
        props.setAtleta(novosJogadores);
        localStorage.setItem('atleta', JSON.stringify(novosJogadores))
    }
    return(
        <div className="colaborador">
            <div className="cabecalho">
                <img src={props.imagem || "./imagens/perfil.jpg"} alt={props.nome}/>
            </div>
            <div className="rodape">
                <h4>{props.nome}</h4>
                <h5>{props.cargo}</h5>
                <h6>{props.titularReserva}</h6>
                <button className="" onClick={removerJogador}>
                    <i className="fi fi-rr-trash"></i> 
                </button>
            </div>
        </div>
    )
}
export default Card;
