import Banner from './components/Banner';
import Formulario from './components/Formulario';
import {React, useState} from 'react';
import Times from './components/time';


function App() {
  const times = [
    {
      nome: "Brasil",
      corPrimaria: "#96E831",
      corSecundaria:"#F0E95C"
    },
    {
      nome:"Argentina",
      corPrimaria: "#1B5AEB",
      corSecundaria:"#6196EB"
    },
    {
      nome:"França",
      corPrimaria: "#BD1E19",
      corSecundaria:"#3B64BD"
    }
  ]

  const [atleta, setAtleta] = useState([]);
  const [erroTecnico, setErroTecnico] = useState(false);
  const [erroCapitao, setErroCapitao] = useState(false);
  const [erroTitular, setErroTitular] = useState(false);

  const adicionarJogador = (atletaEvent) => {
    console.log(atletaEvent)
    const podeEscalar = verificarEscalacao(atletaEvent)
    if (podeEscalar) setAtleta([...atleta, atletaEvent])
  }

  const verificarEscalacao = (atletaEvent) => {
    if (atletaEvent.cargo == 'Tecnico' && atleta.some(atleta => atleta.cargo == 'Tecnico')) {
      setErroTecnico(true);
      setTimeout(() => {
        setErroTecnico(false);
      }, 2000)
      return false;
    } else if (atletaEvent.cargo == 'Capitão' && atleta.some(atleta => atleta.cargo == "Capitão")){
      setErroCapitao(true);
      setTimeout(() => {
        setErroCapitao(false);
      }, 2000);
      return false;
    } else if (count == 3){
      setErroTecnico(true)
      setTimeout(() => {
        setErroTitular(false);
      }, 2000);
      return false
    }
    else return true;
  }
  return (
    <div className="">
        <Banner/>
        <Formulario erroTitular={erroTitular} erroCapitao={erroCapitao} erroTecnico={erroTecnico} times={times.map(time => time.nome )} novoAtleta={atletaEvent => adicionarJogador(atletaEvent)}/>
        {times.map(time => <Times 
          key={time.nome}
          nome={time.nome} 
          corPrimaria={time.corPrimaria} 
          corSecundaria={time.corSecundaria}
          atletas={atleta.filter(atletaEvent => atletaEvent.time === time.nome)}
        /> )}
    </div>
  );
}

export default App;
