import React from "react";
import './card.css';

const Card = ({atleta, setAtleta, imagem, cargo, nome, index, titularReserva, time, mudarCorPrimaria}) => {
    const removerJogador = () => {
        const novosJogadores = [...atleta];
        const jogador = atleta.find((a) => a.index === index)
        const indexx = atleta.indexOf(jogador)
        novosJogadores.splice(indexx, 1);
        setAtleta(novosJogadores);
        localStorage.setItem('atleta', JSON.stringify(novosJogadores))
    }
    return(
        <div className="colaborador">
            <div className="cabecalho" style={{backgroundColor: time.corPrimaria}}>
                <input onChange={evento => mudarCorPrimaria(evento.target.value, time.nome)} value={time.corPrimaria} type="color" className="rounded-full border-0 w-3" ></input>
                <img src={imagem || "./imagens/perfil.jpg"} alt={nome}/>
            </div>
            <div className="rodape">
                <h4>{nome}</h4>
                <h5>{cargo}</h5>
                <h6>{titularReserva}</h6>
                <button className="" onClick={removerJogador}>
                    <i className="fi fi-rr-trash"></i> 
                </button>
            </div>
        </div>
    )
}
export default Card;
