    // chamar com 'onChange={função}' 
    const [valor, setValor] = useState("");
    const pegarValorDoInput = (event) => {
        props.aoAlterado(event.target.value);
        console.log(valor)
    }

    return (
        <div>
            <label>{props.label}</label>
            <input value={props.valor} onChange={pegarValorDoInput} required={props.obrigatorio} placeholder={props.placeholder}></input>
        </div>
    )
}
export default CampoTexto;
