import React from "react";
import CampoTexto from "../CampoTexto";
import Lista from "../listaPosição";
import Botao from "../botao"

const Formulario = () => {
    const posicao = [
        "Atacante",
        "Goleiro",
        "Zagueiro",
        "Tecnico",
        "Capitão"
    ]
    const time = [
        "Brasil",
        "Argentina",
        "França",
        "Alemanha"
    ]
    const aoSalvar = (evento) => {
        //previne o comportamento padrão de recarregar a pagina ao submeter o formulario
        evento.preventDefault() 
        console.log("form foi submetido")
    }
    return(
        <section>
            <form onSubmit={aoSalvar} className="m-8 p-10 bg-slate-300 shadow-md flex flex-col justify-center"> 
            <h1>Preencha os campos para escalar seus times!</h1>
                <CampoTexto obrigatorio={true} placeholder="Nome" label="Nome"/>
                <CampoTexto placeholder="url da imagem" label="url da imagem"/>
                <Lista obrigatorio={true} label="Posição" itens={posicao}/>
                <Lista obrigatorio={true} label="Seleção" itens={time}/>
                <Botao>
                    criar card
                </Botao>
            </form>
        </section>
    )
}
export default Formulario;
