import React from "react";

const CampoTexto = (label, valor, seAlterado, obrigatorio=false, ) => {
    // criar uma função para armazenar o valor do input 
    // chamar com 'onChange={função}'
    return (
        <div className="flex flex-col gap-2">
            <label>{label}</label>
            <input value={valor} onChange={event => props.seAlterado(event.target.value)} required={obrigatorio} placeholder={props.placeholder} className="bg-white p-2 rounded-md"></input>
        </div>
    )
}
export default CampoTexto;
