import React from "react";

const CampoTexto = (props) => {
    // criar uma função para armazenar o valor do input 
    // chamar com 'onChange={função}'
    const aoSalvar = (evento, props) => {
        //previne o comportamento padrão de recarregar a pagina ao submeter o formulario
        evento.preventDefault() 
        props.colaboradorNovo({
            nome,
            cargo,
            time,
            imagem
        })
    }
    return (
        <div>
            <label>{props.label}</label>
            <input value={props.valor} onChange={event => props.alterado(event.target.value)} required={props.obrigatorio} placeholder={props.placeholder}></input>
        </div>
    )
}
export default CampoTexto;
