import React from "react";
import Card from "../card";

const Times = (props) => {
    return(
        props.atletas.length > 0 &&  <section style={{backgroundColor: props.corSecundaria}} className="p-12 text-center ">
           <h3 className="p-3 m-3 font-bold">{props.nome}</h3>
           <div className="flex justify-around flex-wrap gap-3">
                {props.atletas.map(atleta => 
                    <Card 
                        key={atleta.index}
                        atleta={props.atleta}
                        setAtleta={props.setAtleta} 
                        nome={atleta.nome} 
                        cargo={atleta.cargo} 
                        imagem={atleta.imagem}
                        index={atleta.index}
                        titularReserva={atleta.titularReserva}
                    />
                )}
           </div>
        </section>
    )
}
export default Times;
